import * as Constants from '../../constants';
import { toCamelCase, toCapitalCase, toPascalCase } from './utils';

export const getComponentName = (framework, input) => {

    if (framework !== 'react') return input;

    return toPascalCase(input);
}

export const getEventName = (framework, input) => {

    if (framework !== 'react') return input;

    return `on${Constants.PLATFORM_EVENT_PREFIX}${toCapitalCase(input)}`;
}

export const getPropertyName = (framework, input) => {

    if (framework !== 'react') return input;

    return toCamelCase(input);
}
