import React from 'react';
import { observer } from 'mobx-react-lite';
import { useStore } from '@app/hooks';

export const FrameworkSelector: React.FC<any> = observer(() => {

  const store = useStore();

  const frameworks = [
    {
      key: 'react',
      value: 'React',
    },
    {
      key: 'vue',
      value: 'Vue',
    },
    // {
    //     key: 'angular',
    //     value: 'Angular'
    // },
    {
      key: 'javascript',
      value: 'Javascript',
    },
  ];

  const change = (event: any) => {
    store.ui.setFramework(event.target.value);
  }

  return (
    <select value={store.ui.framework} onChange={change}>
      {frameworks.map((item) => (
        <option key={item.key} value={item.key}>
          {item.value}
        </option>
      ))}
    </select>
  );
});
