import React from 'react';
import { Utils } from 'utils';
import { BoxProps } from './box.types';
import styles from './box.module.scss';

export const Box: React.FC<BoxProps> = ({ children, m, mb, ml, mr, mt, mx, my, p, pb, pl, pr, pt, px, py }) => {

  const classes = Utils.classes({
    [styles.box]: true
  });

  return <div className={classes} style={{
    marginTop: mt || my || m,
    marginRight: mr || mx || m,
    marginBottom: mb || my || m,
    marginLeft: ml || mx || m,
    paddingTop: pt || py || p,
    paddingRight: pr || px || p,
    paddingBottom: pb || py || p,
    paddingLeft: pl || px || p,
  }}>{children}</div>;
};
