import React from 'react';
import { applyPolyfills, defineCustomElements } from '@htmlplus/react/loader';
import { useStore } from 'hooks';
import { LayoutDefault } from 'layouts';
import { observer } from 'mobx-react-lite';
import { AppProps } from 'next/app';
import Head from 'next/head';
import { Store, StoreContext } from 'store';
import '../styles/index.scss';

const store = new Store();

applyPolyfills().then(() => defineCustomElements());

const Layout: React.FC = observer(({ children }) => {

  const store = useStore();

  if (store.ui.layout === 'default') return <LayoutDefault>{children}</LayoutDefault>;

  return <>{children}</>;
});

const App: React.FC<AppProps> = ({ Component, children, pageProps }) => {
  return (
    <React.StrictMode>
      <StoreContext.Provider value={store}>
        <Layout>
          <Head>
            <title>HTMLPLUS | DOCS</title>
            <link rel="shortcut icon" href="/public/favicon.ico" />
            <meta
              name="viewport"
              content="width=device-width, initial-scale=1.0, minimum-scale=1.0, maximum-scale=5.0"
            />
          </Head>
          <Component {...pageProps} />
        </Layout>
      </StoreContext.Provider>
    </React.StrictMode>
  );
};

export default App;
