import classNames from 'classnames';

export const classes = classNames;

export const render = (items, level = 0) => {

    return items
        .map((item) => {

            if (Array.isArray(item)) {

                return render(item.filter((item) => item !== null), level + 1);
            }
            else {

                if (typeof item === 'undefined') return null;

                let space = '';

                for (let i = 0; i < level; i++) space += '  ';

                return space + item;
            }
        })
        .filter((item) => item !== null)
        .join('\n');
}

export const toClass = (input?) => {

    if (!input) return input;

    const keys = Object.keys(input);

    const result = {};

    for (let i = 0; i < keys.length; i++) {

        const key = keys[i];

        const value = input[key];

        const text = (typeof value === 'boolean') ? key : `${key}-${value}`;

        result[text] = !!value;
    }

    return result;
}

export const toKebabCase = (input: string) => {
    return input.toLowerCase().replace(/ /g, '-');
}

export const toCamelCase = (input: string) => {

    if (!input) return input;

    // TODO
    return input.replace(/(?:^\w|[A-Z]|\b\w)/g, (word, index) => {
        return index === 0 ? word.toLowerCase() : word.toUpperCase();
    }).replace(/\s+/g, '').replace(/-/g, '');
}

export const toCapitalCase = (input: string) => {

    if (!input) return input;

    return input.replace(/^\w/, (char) => char.toUpperCase());
}

export const toPascalCase = (input: string) => {

    if (!input) return input;

    return input.split('-').map((word) => toCapitalCase(word)).join('');
}

export const toUnit = (input: string | number) => {

    if (input == null || input === '') return undefined;

    if (isNaN(+input!)) return String(input);

    return `${Number(input)}px`;
}
