import React from 'react';
import { observer } from 'mobx-react-lite';
import { useStore } from 'hooks';
import { Utils } from 'utils';
import styles from './framework-selector.module.scss';

export const FrameworkSelector: React.FC<any> = observer(() => {

  const store = useStore();

  const frameworks = [
    {
      key: 'react',
      value: 'React',
    },
    {
      key: 'vue',
      value: 'Vue',
    },
    // {
    //     key: 'angular',
    //     value: 'Angular'
    // },
    {
      key: 'javascript',
      value: 'Javascript',
    },
  ];

  const onChange = (event: any) => store.ui.setFramework(event.target.value);

  const classes = Utils.classes({
    [styles['framework-selector']]: true,
  });

  return (
    <div className={classes}>
      <select value={store.ui.framework} onChange={onChange}>
        {frameworks.map((item) => (
          <option key={item.key} value={item.key}>
            {item.value}
          </option>
        ))}
      </select>
    </div>
  );
});
