import React from 'react';
import { PlusGrid, PlusGridItem } from 'components';
import { Utils } from 'utils';
import styles from './error.module.scss';

export const LayoutError: React.FC = ({ children }) => {

  const classes = Utils.classes({
    [styles['layout-error']]: true,
  });

  return (
    <div className={classes}>
      <PlusGrid style={{ height: '100%' }} alignItems="center" justify="center" >
        <PlusGridItem>
          {children}
        </PlusGridItem>
      </PlusGrid>
    </div >
  );
};
