import React, { useMemo } from 'react';
import { FrameworkSelector, Link, Section, Sidebar, PlusGrid, PlusGridItem } from 'components';
import { components, sidebar } from 'data';
import { useRouter } from 'hooks';
import { Utils } from 'utils';
import styles from './default.module.scss';
import { Box } from 'components/box';

export const LayoutDefault: React.FC = ({ children }) => {

  const router = useRouter();

  const all: Array<any> = sidebar
    .map((item) => {

      if (!item.items) return null;

      return (item.items || []).map((subItem) => ({ ...subItem, category: item.title }));
    })
    .flat();

  // TODO: route service
  const index = all.findIndex(({ route }) => router.location.path === router.path(route.to, route.params));

  const next = all[index + 1];

  const prev = all[index - 1];

  const classes = Utils.classes({
    [styles['layout-default']]: true,
  });

  return (
    <div className={classes}>
      <FrameworkSelector />
      <PlusGrid wrap="off">
        <PlusGridItem xs="auto">
          <Sidebar items={sidebar} />
        </PlusGridItem>
        <PlusGridItem xs="grow">
          {/* TODO:     
            max-width: 768px;
            margin: 0 auto;
            padding: 2rem;
          */}
          <Box>
            {children}
            <Section size="page">
              <PlusGrid justify="between">
                <PlusGridItem xs="auto">
                  {!!prev && (
                    <Link {...prev.route}>
                      PREV
                      <br />
                      {prev.title}
                      <br />
                      {prev.category}
                    </Link>
                  )}
                </PlusGridItem>
                <PlusGridItem xs="auto">
                  {!!next && (
                    <Link {...next.route}>
                      NEXT
                      <br />
                      {next.title}
                      <br />
                      {next.category}
                    </Link>
                  )}
                </PlusGridItem>
              </PlusGrid>
            </Section>
          </Box>
        </PlusGridItem>
      </PlusGrid>
    </div>
  );
};
