import React from 'react';
import { Utils } from 'utils';
import { Link, Text } from 'components';
import { SidebarItem, SidebarProps } from './sidebar.types';
import styles from './sidebar.module.scss';

export const Sidebar: React.FC<SidebarProps> = ({ items = [] }) => {

  const menu = (items: Array<SidebarItem>) => {
    return (
      <div className="sidebar__menu">
        {items.map((item) => (
          <div key={item.title} className="sidebar__menu-item">
            <Text>
              {item.route ? <Link {...item.route}>{item.title}</Link> : item.title}
            </Text>
            {item.items && menu(item.items)}
          </div>
        ))}
      </div>
    );
  };

  const classes = Utils.classes({
    [styles.sidebar]: true,
  });

  return (
    <div className={classes}>
      <Text>HTML PLUS</Text>
      {menu(items)}
    </div>
  );
};
