import React, { useState } from 'react';
import { Box, Drawer, Footer, Header, Icon, Link, PlusGrid, PlusGridItem, Section, Sidebar, Text, Toc } from '@app/components';
import * as Constants from '@app/constants';
import { sidebar } from '@app/data';
import { useRouter } from '@app/hooks';
import { Utils } from '@app/utils';

export const LayoutDefault: React.FC = (props) => {

  const { children } = props;

  const router = useRouter();

  const [open, setOpen] = useState(false);

  const all: Array<any> = sidebar
    .map((item) => {

      if (!item.items) return null;

      return (item.items || []).map((subItem) => ({ ...subItem, category: item.title }));
    })
    .flat();

  const index = all
    .filter((item) => item && item.route)
    .findIndex((item) => router.isActive(item.route.to, item.route.params));

  const next = all[index + 1];

  const prev = all[index - 1];

  const classes = Utils.classes(
    'layout-default'
  );

  return (
    <div className={classes}>
      <Header onMenuClick={() => setOpen(true)} />
      <Drawer open={open} onClose={() => setOpen(false)}>
        <Box p={6}>
          <Text align="center">
            {Constants.PLATFORM_NAME}
          </Text>
        </Box>
        <Sidebar items={sidebar} />
      </Drawer>
      <PlusGrid wrap="off">
        <PlusGridItem xs="auto" hideMdDown>
          <div className="sticky">
            <Sidebar items={sidebar} />
          </div>
        </PlusGridItem>
        <PlusGridItem xs="grow">
          <Box maxWidth={768} m="auto" px={4}>
            {children}
            <Section size="page">
              <PlusGrid justify="between">
                <PlusGridItem xs="auto">
                  {!!prev && (
                    <Text align="start">
                      <Link {...prev.route}>
                        <PlusGrid alignItems="center" gutter="md">
                          <PlusGridItem>
                            <Icon name="prev" />
                          </PlusGridItem>
                          <PlusGridItem>
                            {prev.title}
                            <br />
                            {prev.category}
                          </PlusGridItem>
                        </PlusGrid>
                      </Link>
                    </Text>
                  )}
                </PlusGridItem>
                <PlusGridItem xs="auto">
                  {!!next && (
                    <Text align="end">
                      <Link {...next.route}>
                        <PlusGrid alignItems="center" gutter="md">
                          <PlusGridItem>
                            {next.title}
                            <br />
                            {next.category}
                          </PlusGridItem>
                          <PlusGridItem>
                            <Icon name="next" />
                          </PlusGridItem>
                        </PlusGrid>
                      </Link>
                    </Text>
                  )}
                </PlusGridItem>
              </PlusGrid>
            </Section>
          </Box>
        </PlusGridItem>
        <PlusGridItem className="sticky" xs="auto" hideMdDown>
          <div className="sticky">
            <Toc />
          </div>
        </PlusGridItem>
      </PlusGrid>
      <Footer />
    </div>
  );
};
