import React, { useMemo } from 'react';
import { HpTabs, HpTabsBar, HpTabsTab, HpTabsPanels, HpTabsPanel } from '@htmlplus/react';
import { Code } from 'components';
import { Utils } from 'utils';
import { ExampleProps, ExampleLanguage } from './example.types';
import styles from './example.module.scss';

// TODO:
import * as core from './core';

export const Example: React.FC<ExampleProps> = ({ value }) => {

  const code = value.code;

  const Preview = value.tag as any;

  // TODO:
  // !customElements.get(Preview) && value.preview(core);

  const tabs = useMemo(
    () => {
      return Object.keys(code)
        .map((key) => ({
          key,
          title: Utils.capitalize(key),
          language: ExampleLanguage[key as keyof typeof ExampleLanguage],
          value: code[key],
        }));
    },
    [code]
  );

  const classes = Utils.classes({
    [styles.example]: true,
  });

  return (
    <div className={classes}>
      <HpTabs value="preview">
        <HpTabsBar>
          <HpTabsTab value="preview">Preview</HpTabsTab>
          {tabs.map((item: any) => (
            <HpTabsTab key={item.key} disabled={!item.value} value={item.key}>
              {item.title}
            </HpTabsTab>
          ))}
        </HpTabsBar>
        <HpTabsPanels>
          <HpTabsPanel value="preview">
            <Preview />
          </HpTabsPanel>
          {tabs.map((item: any) => (
            <HpTabsPanel key={item.key} value={item.key}>
              <Code language={item.language}>{item.value}</Code>
            </HpTabsPanel>
          ))}
        </HpTabsPanels>
      </HpTabs>
    </div>
  );
};
