import { Alert, Code, Link, Text } from 'components';
import * as Constants from '../../../constants';

export const renderers = {
    blockquote: (props) => {

        const value = props.node.children[0].children[0].value.trim();

        const token = ((value.match(/\[\w+\]/) || [])[0] || '');

        const type = token.replace(/\[|\]/g, '');

        return <Alert type={type}>{value.replace(token, '')}</Alert>;
    },
    code: (props) => <Code language={props.language}>{props.value}</Code>,
    heading: (props) => {

        const sizes: any = ['title', 'title', 'title', 'title', 'title', 'title'];

        return <Text size={sizes[props.level]}>{props.children}</Text>
    },
    // inlineCode: (props) => <Text color="error" size="body" inline>{props.children}</Text>,
    link: (props) => <Link href={props.href}>{props.children}</Link>,
    paragraph: (props) => <Text size="body">{props.children}</Text>,
};

export const tokenizer = (input) => {

    const tokens = input.match(/\{\{(.*?)\}\}/g) || [];

    tokens.map((token) => {

        const raw = token.replace(/\{|\}/g, '');

        const sections = raw.split(':');

        const type = sections[0];

        const value = sections[1];

        switch (type) {

            case 'CONSTANT':
                input = input.replace(token, Constants[value]);
                break;

            case 'ROUTE':
                input = input.replace(token, value);
                break;
        }
    });

    return input;
}
