import React, { useEffect } from 'react';
import { useStore } from 'hooks';
import { PageProps } from './page.types';

export const Page: React.FC<PageProps> = ({ children, layout }) => {

  const store = useStore();

  useEffect(
    () => {
      store.ui.setLayout(layout);
    },
    [layout]
  );

  return <>{children}</>;
};
