import React, { useMemo, useState } from 'react';
import { observer } from 'mobx-react-lite';
import {
  HpGrid,
  HpGridItem,
  HpTabs,
  HpTabsBar,
  HpTabsPanel,
  HpTabsPanels,
  HpTabsTab,
} from '@htmlplus/react';
import * as constant from 'constant';
import { Example, Markup, Page, Parameters, Section, Text } from 'components';
import { components } from 'data';
import { useStore } from 'hooks';
import { Utils } from 'utils';

// TODO
interface ComponentDetailsProps {
  item: any;
}

export const ComponentDetails: React.FC<ComponentDetailsProps> = observer(({ item }) => {

  const store = useStore();

  const [selected, setSelected] = useState(undefined);

  const parent = item;

  const children: Array<any> = useMemo(
    () => {

      const result = [];

      result.push(parent);

      components
        .filter((component) => !component.parent && component.group === parent?.group)
        .map((component) => result.push(component));

      return result;
    },
    [parent]
  );

  const current: any = useMemo(
    () => {
      return components.find(({ key }) => key === (selected || parent?.key));
    },
    [parent, selected]
  );

  const tabs = useMemo(
    () => {

      const { properties, slots, events, styles, methods } = current;

      return [
        {
          key: 'properties',
          value: 'Properties',
          items: properties,
        },
        {
          key: 'slots',
          value: 'Slots',
          items: slots,
        },
        {
          key: 'events',
          value: 'Events',
          items: events.map((event: any) => ({
            ...event,
            value: event.detail,
            name: `on${constant.PLATFORM_EVENT_PREFIX}${Utils.capitalize(event.name)}`,
          })),
        },
        {
          key: 'styles',
          value: 'Styles',
          items: styles,
        },
        {
          key: 'methods',
          value: 'Methods',
          items: methods.map((method: any) => ({ ...method, value: method.signature })),
        },
      ];
    },
    [current]
  );

  // TODO:
  const examples = useMemo(
    () => {
      try {
        let examples: Array<any> = require(`@htmlplus/examples/src/combine/${parent.key}.js`).examples;
        examples = (examples || []).map((example) => {
          const port = example.ports[store.ui.framework];
          example.code = port;
          example.code.style = example.style;
          return example;
        });
        return examples;
      }
      catch {
        return [];
      }
    },
    [store.ui.framework, parent.key]
  );

  const isDisabled = (key: string) => !current[key].length;

  const isActive = (key: string) => (tabs.find((tab) => !isDisabled(tab.key)) || {}).key === key;

  const hasApi = !!tabs.filter((tab) => isActive(tab.key)).length;

  const hasExamples = !!examples.length;

  const hasReadme = !!parent.readme;

  const hasUsage = !!parent.usage;

  if (!current) return null;

  return (
    <Page layout="default">

      <Section size="page">
        <Text>{parent.title}</Text>
        <Markup>{parent.description}</Markup>
      </Section>

      {hasReadme && (
        <Section size="page">
          <Markup>{parent.readme}</Markup>
        </Section>
      )}

      {hasUsage && (
        <Section size="page">
          <Text>Usage</Text>
          <Text>Usage description</Text>
          {/* <app-usage value={current} /> */}
        </Section>
      )}

      {hasApi && (
        <Section size="page">
          <HpGrid align-items="center" justify="between">
            <HpGridItem>
              <Text>API</Text>
            </HpGridItem>
            <HpGridItem>
              <select
                value={selected}
                disabled={children.length < 2}
                onChange={(event: any) => setSelected(event.target.value)}
              >
                {children.map((item) => (
                  <option key={item.key} value={item.key}>
                    {item.tag}
                  </option>
                ))}
              </select>
            </HpGridItem>
          </HpGrid>
          <Text>
            Select your desired component from below and see the available properties, slots,
            events, styles and methods.
          </Text>
          <HpTabs value="properties">
            <HpTabsBar grow>
              {tabs.map((tab) => (
                <HpTabsTab key={tab.key} disabled={isDisabled(tab.key)} value={tab.key}>
                  <Text>{tab.value}</Text>
                </HpTabsTab>
              ))}
            </HpTabsBar>
            <HpTabsPanels>
              {tabs.map((tab) => (
                <HpTabsPanel key={tab.key} value={tab.key}>
                  <Parameters items={tab.items} />
                </HpTabsPanel>
              ))}
            </HpTabsPanels>
          </HpTabs>
        </Section>
      )}

      {hasExamples && (
        <Section size="page">
          <Text>Examples</Text>
          <Text>Below is a collection of simple to complex examples.</Text>
        </Section>
      )}

      {examples.map((example: any) => (
        <Section key={example.key} size="page">
          <Text>{example.title}</Text>
          <Markup>{example.readme}</Markup>
          <Example value={example} />
        </Section>
      ))}
    </Page>
  );
});
