import React, { useEffect, useMemo } from 'react';
import Prism from 'prismjs';
import { HpGrid, HpGridItem } from '@htmlplus/react';
import { FrameworkSelector, Link, Section, Sidebar } from 'components';
import { components } from 'data';
import { useRouter } from 'hooks';
import { Utils } from 'utils';
import styles from './default.module.scss';

export const LayoutDefault: React.FC = ({ children }) => {

  const router = useRouter();

  // TODO
  useEffect(() => Prism.highlightAll());

  const items = useMemo(
    () => ([
      {
        title: 'UI Components',
        items: components
          .filter((item) => item.parent)
          .map((component) => ({
            // @ts-ignore
            title: component.title + (component.development ? ' (development)' : ''),
            route: {
              to: 'ROUTE:COMPONENT:DETAILS',
              params: {
                key: component.key,
              },
            }
          })),
      },
      {
        title: 'Framework Integrations',
        items: [
          {
            title: 'Overview',
            route: {
              to: 'ROUTE:FRAMEWORK:OVERVIEW',
            },
          },
          {
            title: 'React',
            route: {
              to: 'ROUTE:FRAMEWORK:REACT',
            },
          },
          {
            title: 'Angular',
            route: {
              to: 'ROUTE:FRAMEWORK:ANGULAR',
            },
          },
          {
            title: 'Vue',
            route: {
              to: 'ROUTE:FRAMEWORK:VUE',
            },
          },
          {
            title: 'JavaScript',
            route: {
              to: 'ROUTE:FRAMEWORK:JAVASCRIPT',
            },
          },
        ],
      },
    ]),
    [components]
  );

  const all: Array<any> = useMemo(
    () => {
      return items
        .map((item) => {

          if (!item.items) return null;

          return (item.items || []).map((subItem) => ({ ...subItem, category: item.title }));
        })
        .flat();
    },
    [items]
  );

  const index = useMemo(
    () => all.findIndex((item) => item.path === router.location.path),
    [all, router.location.path]
  );

  const next = useMemo(
    () => all[index + 1],
    [all, index]
  );

  const prev = useMemo(
    () => all[index - 1],
    []
  );

  const classes = Utils.classes({
    [styles['layout-default']]: true,
  });

  return (
    <div className={classes}>
      <FrameworkSelector />
      <HpGrid wrap="off">
        <HpGridItem xs="auto">
          <Sidebar items={items} />
        </HpGridItem>
        <HpGridItem xs="grow">
          {/* TODO: use box */}
          <div className={styles['layout-default__content']}>
            {children}
            <Section size="page">
              <HpGrid justify="between">
                <HpGridItem xs="auto">
                  {!!prev && (
                    <Link to={prev.route.name} params={prev.route.params}>
                      PREV
                      <br />
                      {prev.title}
                      <br />
                      {prev.category}
                    </Link>
                  )}
                </HpGridItem>
                <HpGridItem xs="auto">
                  {!!next && (
                    <Link to={next.route.name} params={next.route.params}>
                      NEXT
                      <br />
                      {next.title}
                      <br />
                      {next.category}
                    </Link>
                  )}
                </HpGridItem>
              </HpGrid>
            </Section>
          </div>
        </HpGridItem>
      </HpGrid>
    </div>
  );
};
