import React, { useState } from 'react';
import { Box, Drawer, Footer, Header, Link, Section, Sidebar, PlusGrid, PlusGridItem, Toc } from 'components';
import { sidebar } from 'data';
import { useRouter } from 'hooks';
import { Utils } from 'utils';
import styles from './default.module.scss';

export const LayoutDefault: React.FC = ({ children }) => {

  const router = useRouter();

  const [open, setOpen] = useState(false);

  const all: Array<any> = sidebar
    .map((item) => {

      if (!item.items) return null;

      return (item.items || []).map((subItem) => ({ ...subItem, category: item.title }));
    })
    .flat();

  // TODO: route service
  const index = all.filter((item) => item && item.route).findIndex(({ route }) => router.location.path === router.path(route.to, route.params));

  const next = all[index + 1];

  const prev = all[index - 1];

  const classes = Utils.classes({
    [styles['layout-default']]: true,
  });

  return (
    <div className={classes}>
      <Header onMenuClick={() => setOpen(true)} />
      <Drawer open={open} onClose={() => setOpen(false)}>
        <div style={{ 'textAlign': 'center', 'padding': '24px' }}>
          HTMLPlus
        </div>
        <Sidebar items={sidebar} />
      </Drawer>
      <PlusGrid wrap="off">
        <PlusGridItem xs="auto" hideMdDown>
          <div className={styles.sticky}>
            <Sidebar items={sidebar} />
          </div>
        </PlusGridItem>
        <PlusGridItem xs="grow">
          <Box maxWidth={768} m="auto" px={4}>
            {children}
            <Section size="page">
              <PlusGrid justify="between">
                <PlusGridItem xs="auto">
                  {!!prev && (
                    <Link {...prev.route}>
                      PREV
                      <br />
                      {prev.title}
                      <br />
                      {prev.category}
                    </Link>
                  )}
                </PlusGridItem>
                <PlusGridItem xs="auto">
                  {!!next && (
                    <Link {...next.route}>
                      NEXT
                      <br />
                      {next.title}
                      <br />
                      {next.category}
                    </Link>
                  )}
                </PlusGridItem>
              </PlusGrid>
            </Section>
          </Box>
        </PlusGridItem>
        <PlusGridItem className={styles.sticky} xs="auto" hideMdDown>
          <div className={styles.sticky}>
            <Toc />
          </div>
        </PlusGridItem>
      </PlusGrid>
      <Footer />
    </div>
  );
};
