import React, { useEffect, useRef } from 'react';
import Prism from 'prismjs';
import 'prismjs/components/prism-jsx';
import { Utils } from 'utils';
import { CodeProps } from './code.types';
import styles from './code.module.scss';

export const Code: React.FC<CodeProps> = ({ children, language }) => {

  const element = useRef(null);

  useEffect(
    () => {
      Prism.highlightAllUnder(element.current);
    }
  );

  const classes = Utils.classes({
    [styles.code]: true
  });

  const classesCode = `${process.browser ? ' ' : ''}language-${language}`;

  return (
    <div ref={element} className={classes}>
      <pre className={classesCode}>
        <code className={classesCode}>
          {(children || '').toString()}
        </code>
      </pre>
    </div>
  );
};
