import React, { useMemo } from 'react';
import NextLink from 'next/link';
import { router } from 'services';
import { Anchor } from './anchor';

interface LinkProps {
  to: string;
  query?: object;
  params?: object;
  href?: string;
  [key: string]: any;
}

export const Link: React.FC<LinkProps> = ({
  to,
  query,
  params,
  href,
  children,
  ...anchorProps
}) => {
  const route = useMemo(() => router.get(to), [to]);
  const path = useMemo(() => router.path(to, params, query), [to, query, params]);

  if (!route && !href) {
    console.error(`Invalid route "${to}"`);
  }
  const { className, onClick, ...nextProps } = anchorProps;

  if (route) {
    return (
      <NextLink {...nextProps} href={route.path} as={path} passHref>
        <Anchor {...anchorProps}>{children}</Anchor>
      </NextLink>
    );
  }

  return (
    <Anchor href={href} {...anchorProps}>
      {children}
    </Anchor>
  );
};
