import React from 'react';
import ReactMarkdown from 'react-markdown';
import { renderers, tokenizer } from './markup.renderers';
import { MarkupProps } from './markup.types';

export const Markup: React.FC<MarkupProps> = ({ children, ignoreParagraph }) => {

  if (ignoreParagraph) renderers.paragraph = (props) => props.children

  return (
    <ReactMarkdown renderers={renderers}>
      {tokenizer(children.toString())}
    </ReactMarkdown>
  );
};
