import { makeAutoObservable } from 'mobx';
import { Store } from './store';
import { Framework, Layout } from './store.ui.types';

export class UiStore {

  framework: Framework = 'react';

  layout?: Layout;

  constructor(readonly root: Store) {

    makeAutoObservable(this, { root: false });

    // TODO
    process.browser && this.setFramework(localStorage.getItem('htmlplus:framework') as any || 'react');
  }

  setFramework(value: Framework) {

    this.framework = value;

    // TODO
    process.browser && localStorage.setItem('htmlplus:framework', value);
  }

  setLayout(value: Layout) {
    this.layout = value;
  }
}
