import { useRouter as useRouterNext } from 'next/router';
import { router } from 'services';
export const useRouter = () => {
  const nextRouter = useRouterNext();

  const push = (name, params, query) => {
    const href = router.href(name, query);
    const path = router.path(name, params, query);

    if (!path) {
      throw new Error('Invalid route name');
    }

    nextRouter.push(href, path);
  };

  const pushUrl = url => {
    const route = router.getByUrl(url);

    if (!route) {
      throw new Error('Invalid url');
    }

    nextRouter.push(router.get(route.name).path, url);
  };

  return {
    location: {
      query: nextRouter.query,
      path: nextRouter.asPath
    },
    push,
    pushUrl,
    href: (name, query) => router.href(name, query),
    path: (name, params, query) => router.path(name, params, query)
  };
};
