import React, { useMemo } from 'react';
import NextLink from 'next/link';
import { router } from 'services';
import { AnchorProps, LinkProps } from './link.types';

const Anchor: React.FC<AnchorProps> = React.forwardRef<HTMLAnchorElement, AnchorProps>(({ href, children, ...props }, ref) => {
  return (
    <a ref={ref} href={href} {...props}>
      {children}
    </a>
  )
});

export const Link: React.FC<LinkProps> = (props) => {

  const {
    to,
    query,
    params,
    href,
    children,
    ...anchorProps
  } = props;

  const route = useMemo(
    () => router.get(to),
    [to]
  );

  const path = useMemo(
    () => router.path(to, params, query),
    [to, query, params]
  );

  if (!route && !href) console.error(`Invalid route "${to}"`);

  const { className, onClick, ...nextProps } = anchorProps;

  if (route) {
    return (
      <NextLink {...nextProps} href={route.path} as={path} passHref>
        <Anchor {...anchorProps}>{children}</Anchor>
      </NextLink>
    );
  }

  return (
    <Anchor href={href} {...anchorProps}>
      {children}
    </Anchor>
  );
};
