import React from 'react';
import { Utils } from 'utils';
import { BoxProps } from './box.types';
import styles from './box.module.scss';

export const Box: React.FC<BoxProps> = ({ children, maxWidth, m, mt, mr, mb, ml, mx, my, p, pt, pr, pb, pl, px, py }) => {

  const classes = Utils.classes({
    [styles.box]: true,
    [styles[`m-${m}`]]: m,
    [styles[`mt-${mt}`]]: mt,
    [styles[`mr-${mr}`]]: mr,
    [styles[`mb-${mb}`]]: mb,
    [styles[`ml-${ml}`]]: ml,
    [styles[`mx-${mx}`]]: mx,
    [styles[`my-${my}`]]: my,
    [styles[`p-${p}`]]: p,
    [styles[`pt-${pt}`]]: pt,
    [styles[`pr-${pr}`]]: pr,
    [styles[`pb-${pb}`]]: pb,
    [styles[`pl-${pl}`]]: pl,
    [styles[`px-${px}`]]: px,
    [styles[`py-${py}`]]: py,
  });

  const style = {
    maxWidth: Utils.toUnit(maxWidth)
  }

  return <div className={classes} style={style}>{children}</div>;
};
