import React, { useMemo, useState } from "react";
import {
  Box,
  Example,
  Link,
  Markup,
  Page,
  Parameters,
  PlusGrid,
  PlusGridItem,
  PlusTabs,
  PlusTabsBar,
  PlusTabsPanel,
  PlusTabsPanels,
  PlusTabsTab,
  Section,
  Text,
  TocItem,
  Icon,
} from "components";
import * as Constants from "../../../constants";
import { components } from "data";
import { useStore } from "hooks";
import { observer } from "mobx-react-lite";
import { Framework, Utils } from "utils";

// TODO
import items from "@htmlplus/examples";

// TODO
interface ComponentDetailsProps {
  item: any;
}

const ComponentDetails: React.FC<ComponentDetailsProps> = observer(
  ({ item }) => {
    const store = useStore();

    const [selected, setSelected] = useState(undefined);

    const parent = item;

    const children: Array<any> = useMemo(() => {
      const result = [];

      result.push(parent);

      components
        .filter(
          (component) => !component.main && component.group === parent?.group
        )
        .map((component) => result.push(component));

      return result;
    }, [parent]);

    const current: any = useMemo(() => {
      return components.find(({ key }) => key === (selected || parent?.key));
    }, [parent, selected]);

    const tabs = useMemo(() => {
      const { properties, slots, events, styles, methods } = current;

      return [
        {
          key: "properties",
          value: "Properties",
          items: properties.map((property) => ({
            ...property,
            name: Framework.getPropertyName(store.ui.framework, property.name),
          })),
        },
        {
          key: "slots",
          value: "Slots",
          items: slots,
        },
        {
          key: "events",
          value: "Events",
          items: events.map((event: any) => ({
            ...event,
            value: event.detail,
            name: Framework.getEventName(store.ui.framework, event.name),
          })),
        },
        {
          key: "styles",
          value: "Styles",
          items: styles,
        },
        {
          key: "methods",
          value: "Methods",
          items: methods.map((method: any) => ({
            ...method,
            value: method.signature,
          })),
        },
      ];
    }, [current, store.ui.framework]);

    // TODO:
    const examples = useMemo(() => {
      const examples = [];

      const current = items.find((example) => example.key === parent.key);

      parent.examples.map((example) => {
        example = JSON.parse(JSON.stringify(example));

        const item = current.examples.find((x) => x.key === example.key);

        const preview = item.ports.preview.script;

        const port = item.ports[store.ui.framework];

        const style =
          example.codes.find((code) => code.key === "style")?.value || "";

        example.code = { preview, ...port, style };

        examples.push(example);
      });

      return examples;
    }, [store.ui.framework, parent.key]);

    const isDisabled = (key: string) => !current[key].length;

    const isActive = (key: string) =>
      (tabs.find((tab) => !isDisabled(tab.key)) || {}).key === key;

    const hasApi = !!tabs.filter((tab) => isActive(tab.key)).length;

    const hasExamples = !!examples.length;

    const hasReadme = !!parent.readme;

    if (!current) return null;

    return (
      <Page layout="default">
        <Section size="page">
          <Text size="1">{parent.title}</Text>
        </Section>

        <Section size="page">
          <PlusGrid gutter="xs">
            <PlusGridItem>
              <Text size="label">
                Last update: {new Date(parent.lastModified).toDateString()},
              </Text>
            </PlusGridItem>
            <PlusGridItem>
              <Text size="label">
                Size: {(parent.size / 1024).toFixed(2)} KB
              </Text>
            </PlusGridItem>
          </PlusGrid>
          <Box mb={16} />
        </Section>

        <Section size="page">
          <Markup>{parent.description}</Markup>
        </Section>

        {hasReadme && (
          <Section size="page">
            <Markup>{parent.readme}</Markup>
          </Section>
        )}

        <Section size="page">
          <Text size="2">
            <TocItem>Usage</TocItem>
          </Text>
          <Text size="body">
            Follow the tutorials&nbsp;
            <Link to={`ROUTE:FRAMEWORK:${store.ui.framework.toUpperCase()}`}>
              here
            </Link>
            &nbsp; to use the <code>{Constants.PLATFORM_NAME}</code> library
            on&nbsp;
            <code>{Utils.toCapitalCase(store.ui.framework)}-based</code>{" "}
            projects.
          </Text>
        </Section>

        {hasApi && (
          <Section size="page">
            <PlusGrid align-items="center" justify="between">
              <PlusGridItem>
                <Text dense size="2">
                  <TocItem>API</TocItem>
                </Text>
              </PlusGridItem>
              <PlusGridItem>
                <select
                  value={selected}
                  disabled={children.length < 2}
                  onChange={(event: any) => setSelected(event.target.value)}
                >
                  {children.map((item) => (
                    <option key={item.key} value={item.key}>
                      {Framework.getComponentName(store.ui.framework, item.tag)}
                    </option>
                  ))}
                </select>
              </PlusGridItem>
            </PlusGrid>
            <Text size="paragraph">
              Select your desired component from below and see the available
              properties, slots, events, styles and methods.
            </Text>
            <PlusTabs value="properties">
              <PlusTabsBar grow>
                {tabs.map((tab) => (
                  <PlusTabsTab
                    key={tab.key}
                    disabled={isDisabled(tab.key)}
                    value={tab.key}
                  >
                    <Text>{tab.value}</Text>
                  </PlusTabsTab>
                ))}
              </PlusTabsBar>
              <PlusTabsPanels>
                {tabs.map((tab) => (
                  <PlusTabsPanel key={tab.key} value={tab.key}>
                    <Parameters items={tab.items} />
                  </PlusTabsPanel>
                ))}
              </PlusTabsPanels>
            </PlusTabs>
          </Section>
        )}

        {hasExamples && (
          <Section size="page">
            <Text size="2">
              <TocItem>Examples</TocItem>
            </Text>
            <Text size="body">
              Below is a collection of simple to complex examples.
            </Text>
          </Section>
        )}

        {examples.map((example: any) => (
          <Section key={example.key} size="page">
            <Text size="3">
              <TocItem>{example.title}</TocItem>
            </Text>
            <Text size="paragraph">
              <Markup>{example.readme}</Markup>
            </Text>
            <Example value={example} />
          </Section>
        ))}

        <Section size="page">
          <PlusGrid alignContent="center" justify="start" gutter="xs">
            <PlusGridItem>
              <Icon name="git" />
              {/* <img src="/assets/logo/github.png" alt="github logo" height="16px" width="16px" /> */}
            </PlusGridItem>
            <PlusGridItem>
              <Text size="label">
                github.com/htmlplus/.../components/{parent.key}
              </Text>
            </PlusGridItem>
          </PlusGrid>
        </Section>
        {/* size: {((parent.size || 1024) / 1024).toFixed(1)} KB */}
      </Page>
    );
  }
);

export default ComponentDetails;

export const getStaticProps = async ({ params }) => {
  const { key } = params;

  const component = components.find((item) => item.key === key);

  return {
    props: {
      item: component,
    },
  };
};

export const getStaticPaths = async () => {
  return {
    paths: components.map((item) => `/component/${item.key}`),
    fallback: false,
  };
};
