import React from 'react';
import { Utils } from 'utils';
import { TextProps } from './text.types';
import styles from './text.module.scss';

export const Text: React.FC<TextProps> = ({ children, color, inline, size }) => {

  const classes = Utils.classes({
    [styles.text]: true,
    [styles['text--inline']]: inline,
    [styles[`text--size-${size}`]]: !!size,
    [styles[`text--color-${color}`]]: !!color,
  });

  let Tag;

  switch (size) {

    case '1':
    case '2':
    case '3':
    case '4':
    case '5':
    case '6':
      Tag = `h${size}` as any;
      break;

    case 'paragraph':
      Tag = 'p' as any;
      break;

    default:
      Tag = 'div' as any;
      break;
  }

  return <Tag className={classes}>{children}</Tag>;
};
